    }

  return (
    <Container>
        <Arrow direction="left" onClick={()=> handleClick("left")}>
            <ArrowBackIosNewIcon/>
        </Arrow>
        <Wrapper>
            {sliderItems.map((item) => (
                <Slide bg={item.bg}>
                <ImageContainer>
                    <Image src={item.img} alt={item.title} />
                </ImageContainer>
